//CONVERTED FROM CATID's MERV BOT (www.mervbot.com)


/*jslint node: true */
"use strict";


var LightLcg = function (seed) {
    this._seed = seed || 0;
};

LightLcg.prototype.getNext = function () {
    this._seed *= 0x343FD;	// Has some nice properties, but
    this._seed += 0x269EC3;	//  ultimately fails to pass any
                             //  randomality test.
    return ((this._seed >> 10) & 0x7FFF);	// Return the high word
};

module.exports = LightLcg;